import axios from "../../config/axios"

const apiLogin = async (data) => {

    return axios.get("/public/api/v1/login"+data ,
    {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("__sessionToken"),
        },
      }
    )

}   

  export {
      apiLogin
  }
