import React, { Component } from 'react'
import Edit from "./EditMember/EditMember"
import axios from 'axios'

import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

import './Member.css'

const apiSuser = 'searchuser'


export default class Member extends Component {

    constructor(props){
        super(props)    
        this.renderEditUser = this.renderEditUser.bind(this)
        this.state = {

            tableData :[{
                id:"",
                firstname:"",
                lastname:"",
                email:"",
                tel:"",
                birthday:""
            }],
            id:""

        }
      }

    componentDidMount = () => {

        axios.get(apiSuser)

          .then(
          res => {

            this.setState({
                id: res.data[0].id,
                tableData: res.data
            })

          },
          err => {
            console.log(err)
          }
        )
      }

    renderEditUser(){
        console.log("id",this.state.id)
    }

    render() {

        const {tableData} = this.state ;

        console.log(tableData)

        return (
            <div className="input-text">
            <div className="roommng">
                <button>สมาชิก</button> 
            </div>

            <table className="table table-condensed table-hover" border='1' width='95%' >                <tbody>
                <tr align="center" >
                        <td>ขื่อ-นามสกุล</td>
                        <td>E-mail</td>
                        <td>เบอร์โทรศัพท์</td>
                        <td>แก้ไขข้อมูล</td>
                    </tr>
                    {this.state.tableData.map((note)=>{
                        return (
                                <tr ker={note.id}>
                                    <td>{note.firstname}-{note.lastname} </td>
                                    <td>{note.email}</td>
                                    <td>{note.tel}</td>
                                </tr>
                        )
                    })}
                </tbody>
            </table>

    </div>
        )
    }
}
