import { Component } from "react";
import Nav from "../Nav/nav";

import "./shop.css"

import {
  Switch,
} from "antd";

export default class Shop extends Component {
//   render() {
//     return(
//       <div className="fx-n">
//         <Nav />
//         <div className="txt-h">
//           Shop
//         </div>
//         <div className="inp-img-logo-shop">

//         </div>
//       </div>
//     )
//   }
// };

constructor(props) {
  super(props);
  this.state = {
    file: '',
    imagePreviewUrl: '',
    oos:false,
};
}

putChargerService = (value) => {
  this.setState({ oos: value });

  const swalWithBootstrapButtons = Swal.mixin({
    customClass: {
      confirmButton: "btn btn-success btn-confirm",
      cancelButton: "btn btn-danger btn-cancel",
      title: "txt-ask",
      text: "des-txt",
    },
    buttonsStyling: false,
  });

  swalWithBootstrapButtons
    .fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Confirm",
      cancelButtonText: "Cancel",
      reverseButtons: false,
    })
    .then((result) => {
      console.log(result)
      if (result.value) {
        apiEditService(this._useQueryString().id,this.state.oos)
          .then((response) => {

            console.log(response)

            setTimeout(() => {
              window.location.reload(false);
            }, 1000);
          })
          .catch((error) =>{ 

            console.log("apiEditService: ", error)


          });
      } else {
        this.setState({ oos: !value });
      }
    });
}

_handleSubmit(e) {
  e.preventDefault();
  // TODO: do something with -> this.state.file
  console.log('handle uploading-', this.state.file);
}

_handleImageChange(e) {
  e.preventDefault();

  let reader = new FileReader();
  let file = e.target.files[0];

  reader.onloadend = () => {
    this.setState({
      file: file,
      imagePreviewUrl: reader.result
    });
  }

  reader.readAsDataURL(file)
}

render() {
  let {imagePreviewUrl} = this.state;
  let $imagePreview = null;
  if (imagePreviewUrl) {
    $imagePreview = (<img src={imagePreviewUrl} />);
  } else {
    $imagePreview = (<div className="tpview" ><div className="previewText">Please select an Image for Preview</div></div>);
  }

  return (
    <div className="fx-n">
      <Nav />
      <div className="previewComponent">
      <div className="im-view" >
        <div className="imgPreview">
          {$imagePreview}
        </div>
      </div>
      <form onSubmit={(e)=>this._handleSubmit(e)}>

      <div className="box-re">
                        <Switch
                          className="switch-charger"
                          checked={this.state.oos}
                          checkedChildren={("ซ่อมบำรุง")}
                          unCheckedChildren={("พร้อมใช้งาน")}
                          onChange={(value) => {
                            this.putChargerService(value);  
                          }}
                        />
        </div>
        <div className="input-img" >
          <input className="fileInput" 
            type="file" 
            onChange={(e)=>this._handleImageChange(e)} />
        </div>

        <div className="input-nameshop">
          <label> ชื่อร้าน : </label>
          <input className="fileinput" type="text" />
        </div>

        <div className="input-namemanager">
        <label> ชื่อเจ้าของร้าน : </label>
          <input className="fileinput" type="text" />
        </div>

        <div className="input-tel">
        <label> โทร : </label>
          <input className="fileinput" type="text" />
        </div>

        <div className="input-contact">
        <label> ที่ตั้ง </label>
          <div>
            <label>ที่อยู่ : </label>
            <input className="fileinput" type="text" />
          </div>
          <div>
            <label>ตำบล : </label>
            <input className="fileinput" type="text" />
          </div>
          <div>
            <label>อำเภอ : </label>
            <input className="fileinput" type="text" />
          </div>
          <div>
            <label>จังหวัด : </label>
            <input className="fileinput" type="text" />
          </div>
          <div>
            <label>รหัสไปรษณี : </label>
            <input className="fileinput" type="text" />
          </div>
        </div>

        <div className="input-location">
          <div className="lat" >
            <label>Lat : </label>
            <input type="text" />
          </div>
          <div className="log" >
            <label>Long : </label>
            <input type="text" />
          </div>
        </div>




      <div className="btn-update-data">
      <button className="submitButton" 
            type="submit" 
            onClick={(e)=>this._handleSubmit(e)}>Upload</button>
      </div>

      </form>
    </div>
    </div>
  )
}
}
