import React, { Component } from 'react'
import { CgPen } from "react-icons/cg";
import Edit from "./EditMember/EditMember"
import TBDA from "./datatablea/Datatable";
import axios from 'axios'

import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

import './Member.css'

const apiSuser = 'http://127.0.0.1:9999/searchuser'


export default class Member extends Component {

    constructor(props){
        super(props)

        this.renderTableData = this.renderTableData.bind(this)

        this.state = {

            tableData :[{
                id:"",
                firstname:"",
                lastname:"",
                email:"",
                tel:"",
                birthday:""
            }]

        }
      }

    componentDidMount = () => {

        console.log("==componentDidMount==")

        axios.get(apiSuser)

          .then(
          res => {

            this.setState({
                tableData: res.data
            })
            console.log(this.state.tableData)

          },
          err => {
            console.log(err)
          }
        )
      }

      renderTableData(){
        console.log("id",this.state.id)
        console.log("firstname",this.state.firstname)
        console.log("lastname",this.state.lastname)
        console.log("email",this.state.email)     
    }

    renderTableHeader(){
        return Object.keys(this.state.tableData[0]).map(attr => <th key={attr}>{attr.toUpperCase()}</th>)
    }

    renderTableRows = () => {
        return this.state.tableData.map(user => {
            return (
                <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.email}</td>
                    <td>{user.firstname}</td>
                    <td>{user.lastname}</td>
                    <id>{user.tel}</id>
                </tr>
            )
        })
    }

    render() {

        const {tableData} = this.state ;
        console.log("tabledata",tableData)

        return tableData.length > 0 
        ?(
            <div className="input-text">
            <div className="roommng">
                <button>สมาชิก</button> 
                <input type="text" placeholder="Search" style={{marginLeft: '3%'}} />
            </div>
            <table className="table table-condensed table-hover" border='1' width='95%' >

                <tbody>
                    <tr align="center" >
                        <td>ขื่อ-นามสกุล</td>
                        <td>วันเกิด</td>
                        <td>เบอร์โทรศัพท์</td>
                        <td>E-mail</td>
                        <td>แก้ไขข้อมูล</td>
                    </tr>

                    <tr align="center">
                        {this.renderTableRows()}

                        <td><Link to={'/'}> <CgPen /></Link></td>
                    </tr>

                </tbody>

            </table>
            <Switch>
                <Route exact path='/' component={Edit} />
            </Switch>

    </div>
        ) : (
            <div>
                No users.
            </div>
        )


    }
}
